(2, 'Футер', 'footer', '    <footer>\r\n      <div class=\"footer-container\">\r\n        <div class=\"content\">\r\n          <div class=\"footer-group\">\r\n            <p class=\"footer-titles\">Покупателям</p>\r\n            <ul>\r\n              <li>\r\n                <a href=\"help.php#help1\">Как оформить заказ</a>\r\n              </li>\r\n              <li>\r\n                <a href=\"help.php#help2\">Способы оплаты</a>\r\n              </li>\r\n              <li>\r\n                <a href=\"help.php#help5\">Доставка</a>\r\n              </li>\r\n            </ul>\r\n          </div>\r\n\r\n          <div class=\"footer-group\">\r\n            <p class=\"footer-titles\">Связь с нами</p>\r\n            <ul>\r\n              <li>\r\n                <p class=\"footer-text\">\r\n                  <b>+7 987 654-32-10</b> <br />(c 08:00 до 22:00)\r\n                </p>\r\n              </li>\r\n              <li>\r\n                <a href=\"stores.php\"> Адреса магазинов в г. Белгород</a>\r\n              </li>\r\n            </ul>\r\n          </div>\r\n        </div>\r\n        <p class=\"footer-copyright\">&copy; 2024 TecnicQ. Все права защищены.</p>\r\n      </div>\r\n    </footer>\r\n\r\n    <script src=\"../scripts/header.js\"></script>\r\n    <script src=\"../scripts/logo.js\"></script>\r\n    <script src=\"../scripts/search.js\"></script>\r\n  </body>\r\n</html>\r\n'),
